		$('#bt_video_appointment').bind('click', function(e) {
			if ($('#perms').is(":checked")){
				var perms = 1;
			}
			else
			{
				var perms = 0;
			}
			e.preventDefault();
			$.ajax({
				url: 'index.php?route=product/video_appointment/bookAppointment',
				type: 'post',
				dataType: 'json',
				data: $('#video_appointment_form').serialize()+'&perms=' + perms + '&g-recaptcha-response=' + $('#g-recaptcha-response').val(),
				beforeSend: function() {
					$('.alert').remove();
					$('#bt_video_appointment').attr('disabled', true);
					$("#bt_video_appointment").html('Loading...');
				},
				success: function(data) {
					if (data['error']) {
						var result = data['error'];
						$.map(result, function(k, v) {
							setTimeout(function(){ $("input[name="+v+"]").after('<div class="alert alert-danger" style="margin-bottom:15px;">' + k + '</div>')}, 1000);
						});
					}
					if (data['success']) {
						$('#step1').hide();
						$('#step3').hide();
						$('#step2').hide();
						$('#step4').show();
						$('#video_appointment_form')[0].reset();
					}
				},
				complete: function() {
					setTimeout(function () {
                   		$("#bt_video_appointment").html('book a video appointment');				
						$('#bt_video_appointment').attr('disabled', false);
                 	}, 1000);
				},
			});
		});
});

