function getFomattedDate(selcted_date){
	var jnewd = new Date(selcted_date);

	var sel_day = jnewd.getDate();
	var sel_month = jnewd.getMonth()+1;
	var sel_year = jnewd.getFullYear(); 
	var sel_date = sel_day+'/'+jnewd.getMonth()+1+'/'+jnewd.getFullYear();
	
	return(sel_date);
}

