		e.preventDefault();  
		//$("a:first-of-type").removeClass('chev');				

		var $this = $(this);
		if ($this.next().hasClass('show1')) {
			$this.next().removeClass('show1');
			$this.next().slideUp(150);
			$this.toggleClass('chev');
