<script>
// Set the date we're counting down to

var countDownDate = new Date('December 5, 2022 23:59:59').getTime(); 

var store_id = '7';
var language_id = '8';

// Update the count down every 1 second
var x = setInterval(function() {

  // Get todays local date 
  var d = new Date();
  
  //get local time
  var localTime = d.getTime();
  
  // obtain local UTC offset and convert to msec
var localOffset = d.getTimezoneOffset() * 60000;

// obtain UTC time in msec
var utc = localTime + localOffset;

 if(store_id == 8) {
	var offset = 13;
 } 
 else { 
	if(store_id == 7 || store_id == 21) {
		var offset = 11;
	} 
	else {
		 if(store_id == 1 || store_id == 9 || store_id == 4 || store_id == 10) {
			var offset = 0;
		} 
		else {
			if(store_id == 0 || store_id == 6)
				var offset = -5;
		    else
			    var offset = 1; 
			}
		}
	}

var newtime = utc + (3600000*offset);

var now = new Date(newtime).getTime();

  // Find the distance between now and the count down date
  var distance = countDownDate - now;

  // Time calculations for days, hours, minutes and seconds
  var days = Math.floor(distance / (1000 * 60 * 60 * 24));
  var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  var seconds = Math.floor((distance % (1000 * 60)) / 1000);

  // Display the result in the element with id="demo"
  if(store_id == 3 || language_id == 13)
  {
  	document.getElementById("bf_timer").innerHTML = days + "j " + hours + "h "  + minutes + "m " + seconds + "s ";
  	document.getElementById("bf_timer2").innerHTML = days + "j " + hours + "h "  + minutes + "m " + seconds + "s ";
  } else if(store_id == 2 || language_id == 15) {
  	document.getElementById("bf_timer").innerHTML = days + "T. " + hours + "Std. "  + minutes + "Min. " + seconds + "Sek. ";
  	document.getElementById("bf_timer2").innerHTML = days + "T. " + hours + "Std. "  + minutes + "Min. " + seconds + "Sek. ";
  } else if(store_id == 18 || store_id == 19 ) {
  	document.getElementById("bf_timer").innerHTML = days + "d " + hours + "t "  + minutes + "m " + seconds + "s ";
  	document.getElementById("bf_timer2").innerHTML = days + "d " + hours + "t "  + minutes + "m " + seconds + "s ";
  } else {
  	document.getElementById("bf_timer").innerHTML = days + "d " + hours + "h "  + minutes + "m " + seconds + "s ";
  	document.getElementById("bf_timer2").innerHTML = days + "d " + hours + "h "  + minutes + "m " + seconds + "s ";
  }

  // If the count down is finished, write some text 
  if (distance < 0) {
  	clearInterval(x);
  	document.getElementById("bf_timer").innerHTML = "";
  	document.getElementById("bf_timer2").innerHTML = "";
  }
}, 1000);
</script>
