<script>
/**
 * Call this function when a user clicks on a product link. This function uses the event
 * callback datalayer variable to handle navigation after the ecommerce data has been sent
 * to Google Analytics.
 * @param {Object} productObj An object representing a product.
 */

 

function clickOnProduct(productObj) {

// var dataLayer = dataLayer || [];

  dataLayer.push({
    'event': 'ProductClickEvent',
    'ecommerce': {
      'click': {
        'actionField': {'list': productObj.page_action},      // Optional list property.
        'products': [{
          'name': productObj.name,                      // Name or ID is required.
          'id': productObj.model,
          'price': productObj.price_default,
          'brand': 'DiamondsFactory',
          'category': productObj.category,
          'variant': productObj.sku,
          'position': productObj.ider
         }]
       }
     }
  });  
}

function addToCartDataLayer(productObj) {

// var dataLayer = dataLayer || [];

  dataLayer.push({
	  'event': 'AddToCartEvent',
	  'ecommerce': {
		'currencyCode': productObj.currency_code,
		'add': {                                // 'add' actionFieldObject measures.
		  'products': [{                        //  adding a product to a shopping bag.
			'name': productObj.name,
			'id': productObj.model,
			'price': productObj.price,
			'brand': 'DiamondsFactory',
			'category': productObj.category,
			'variant': productObj.sku,
			'quantity': 1
		   }]
		}
	  }
	});
  
}

function secureCheckoutDataLayer(productObj) {

// var dataLayer = dataLayer || [];

  dataLayer.push({
	  'event': 'SecureCheckoutEvent',
	  'ecommerce': {
		'currencyCode': productObj.currency_code,
		'add': {                                // 'add' actionFieldObject measures.
		  'products': [{                        //  adding a product to a shopping bag.
			'name': productObj.name,
			'id': productObj.model,
			'price': productObj.price,
			'brand': 'DiamondsFactory',
			'category': productObj.category,
			'variant': productObj.sku,
			'quantity': 1
		   }]
		}
	  }
	});
  
}

function RemoveFromCartDataLayer(productObj) {
// var dataLayer = dataLayer || [];

  dataLayer.push({
	  'event': 'removeFromCart',
	  'ecommerce': {
		'remove': {                               // 'remove' actionFieldObject measures.
		  'products': [{                          //  removing a product to a shopping bag.
			  'name': productObj.name,
			  'id': productObj.model,
			  'price': productObj.price,
			  'brand': 'DiamondsFactory',
			  'category': productObj.category,
			  'variant': productObj.sku,
			  'quantity': productObj.quantity
		  }]
		}
	  }
	});
  
}
