function deletecookie(name,value,days)
{
	var date = new Date();
	date.setTime(date.getTime()+(days*24*60*60*1000));
	var expires = "; expires="+date.toGMTString();
	document.cookie = name+"="+value+expires+"; path=/";
}

function toggleNav() {
	var x = document.getElementById("mySidenav");
	$('.mytooltiptext').hide();
	if(x.style.right === '0px'){
		x.style.right = '-514px';
		if (document.cookie.indexOf('activetab') > -1 ) {
			$('#mySidearrow').show();
		}
	} else {

		if(document.getElementById('sel_service').innerText == '' || document.getElementById('sel_service').innerText == '')
			document.getElementById('service_order').style.display = "block";
		else
			document.getElementById('service_order').style.display = "none";

		x.style.right = '0px';
		$('#mySidearrow').hide();
	}
}

function getcookie()
	{
	var cookiearray = document.cookie.split("; ");
	for(var i = 0; i < cookiearray.length; i++)
	{
		var str = cookiearray[i];
		if(str.substr(0,str.indexOf('=')) == "valueselected")
		{
			document.getElementById("sel_service").innerText = str.substr(str.indexOf('=')+1);
			document.getElementById('service_required').value = str.substr(str.indexOf('=')+1);
			if(document.getElementById('sel_service').innerText == '' || document.getElementById('sel_service').innerText == '')
				document.getElementById('service_order').style.display = "block";
			else
				document.getElementById('service_order').style.display = "none";
		}
		if(str.substr(0,str.indexOf('=')) == "dateselected")
		{
			document.getElementById('schedule_date').value = str.substr(str.indexOf('=')+1);
			document.getElementById("sel_date").innerText = str.substr(str.indexOf('=')+1);
		}
		if(str.substr(0,str.indexOf('=')) == "duration")
		{
			document.getElementById('duration').value = str.substr(str.indexOf('=')+1);
		}
		if(str.substr(0,str.indexOf('=')) == "locationselected")
		{
			document.getElementById('sel_location').innerHTML = str.substr(str.indexOf('=')+1);
		}
		if(str.substr(0,str.indexOf('=')) == "loc_selected")
		{
			document.getElementById('location_val').value = str.substr(str.indexOf('=')+1);
		}
		if(str.substr(0,str.indexOf('=')) == "locationtext")
		{
			document.getElementById('change_loc').innerHTML = str.substr(str.indexOf('=')+1);
		}
		if(str.substr(0,str.indexOf('=')) == "timeselected")
		{
			document.getElementById('schedule_time').value = str.substr(str.indexOf('=')+1);
			document.getElementById("sel_time").innerText = str.substr(str.indexOf('=')+1);
		}
	}
	calltab();
	}
function calltab()
{
	var cookiearray = document.cookie.split("; ");
	for(var i = 0; i < cookiearray.length; i++)
		{
		var namevaluearray = cookiearray[i].split("=");
		if(namevaluearray[0] == "activetab")
			{
			  $('.contenttab').show();
			  $("#myTab li a").removeClass("selected");
			  $("a#" + namevaluearray[1]).addClass("selected");
			if(namevaluearray[1] == 'location')
			  {
				$('#myTab a')[0].click();
			  }
			if(namevaluearray[1] == 'service')
			  {
				$("#button-location").trigger("click");
				$('#myTab a')[1].click();
  			  }
			if(namevaluearray[1] == 'schedule')
			  {
				$("#back-schedule").trigger("click");
				$('#myTab li.service').removeClass('noclick');
				$('#myTab li.schedule').removeClass('noclick');
				$('#serviceli li').find('a').css('color','#818181');
				$('#serviceli li[rel="'+ document.getElementById('service_required').value + '-'+ document.getElementById('duration').value +'"]').find('a').css('color','#ce9500');
			  }
			if(namevaluearray[1] == 'confirm')
				{
					$("#timings li[rel='" + document.getElementById('schedule_time').value + "']").trigger("click");
					$('#serviceli li').find('a').css('color','#818181');
					$('#serviceli li[rel="'+ document.getElementById('service_required').value + '-'+ document.getElementById('duration').value +'"]').find('a').css('color','#ce9500');
					$('#myTab a')[3].click();
					$('#myTab').find('li').removeClass('noclick');
				}
			}
		}
}
$( window ).on('load', function() {
	if (document.cookie.indexOf('activetab') > -1 ) {
		$('#mySidearrow').show();
		getcookie();
	}
});
